import React from 'react';
import { NavLink, useLocation } from 'react-router-dom';
import {
  HomeIcon,
  ClipboardIcon,
  UserGroupIcon,
  MoonIcon,
  UserIcon,
  UsersIcon,
} from '@heroicons/react/24/outline';
import Logo from '../common/Logo';

interface NavItem {
  name: string;
  icon: React.ComponentType<{ className?: string }>;
  path: string;
}

const navItems: NavItem[] = [
  { name: 'Dashboard', icon: HomeIcon, path: '/dashboard' },
  { name: 'Chores', icon: ClipboardIcon, path: '/chores' },
  { name: 'Group Members', icon: UsersIcon, path: '/members' },
  { name: 'Guests', icon: UserGroupIcon, path: '/guests' },
  { name: 'Quiet Time', icon: MoonIcon, path: '/quiet-time' },
  { name: 'Profile', icon: UserIcon, path: '/profile' },
];

const Navbar: React.FC = () => {
  const location = useLocation();

  return (
    <nav className="fixed top-0 left-0 h-screen w-64 bg-gray-900 text-white p-4">
      <div className="flex items-center gap-3 mb-8">
        <Logo className="w-8 h-8" />
        <span className="text-xl font-semibold">RoomieSync</span>
      </div>
      <div className="space-y-2">
        {navItems.map((item) => {
          const Icon = item.icon;
          const isActive = location.pathname === item.path;
          return (
            <NavLink
              key={item.name}
              to={item.path}
              className={`flex items-center gap-3 px-4 py-2 rounded-lg transition-colors ${
                isActive
                  ? 'bg-primary-500 text-white'
                  : 'text-gray-300 hover:bg-gray-800 hover:text-white'
              }`}
            >
              <Icon className="w-5 h-5" />
              <span>{item.name}</span>
            </NavLink>
          );
        })}
      </div>
    </nav>
  );
};

export default Navbar;
