import React, { useState } from 'react';
import { Dialog } from '@headlessui/react';

// Define the avatar interface
interface Avatar {
  id: number;
  url: string;
  alt: string;
}

// Pre-validated avatar styles that work well
const avatarStyles = [
  { style: 'adventurer', total: 25 },
  { style: 'bottts', total: 25 }
] as const;

// Generate 50 diverse avatars using different styles
const generateAvatars = (): Avatar[] => {
  const avatars: Avatar[] = [];
  let id = 1;

  avatarStyles.forEach(({ style, total }) => {
    for (let i = 1; i <= total; i++) {
      avatars.push({
        id: id++,
        url: `https://api.dicebear.com/7.x/${style}/svg?seed=${style}${i}`,
        alt: `${style.charAt(0).toUpperCase() + style.slice(1)} avatar ${i}`
      });
    }
  });

  return avatars;
};

const avatars = generateAvatars();

interface AvatarSelectionProps {
  selectedUrl: string;
  onSelect: (url: string) => void;
}

const AvatarSelection: React.FC<AvatarSelectionProps> = ({ selectedUrl, onSelect }) => {
  const [isOpen, setIsOpen] = useState(false);

  return (
    <>
      <button
        onClick={() => setIsOpen(true)}
        className="w-32 h-32 rounded-full border-2 border-gray-300 hover:border-primary-500 transition-colors duration-200 overflow-hidden flex items-center justify-center"
      >
        {selectedUrl ? (
          <img
            src={selectedUrl}
            alt="Selected avatar"
            className="w-full h-full object-cover"
          />
        ) : (
          <div className="text-gray-400">
            Choose Avatar
          </div>
        )}
      </button>

      <Dialog
        open={isOpen}
        onClose={() => setIsOpen(false)}
        className="relative z-50"
      >
        <div className="fixed inset-0 bg-black/30" aria-hidden="true" />

        <div className="fixed inset-0 flex items-center justify-center p-4">
          <Dialog.Panel className="mx-auto max-w-3xl w-full bg-white dark:bg-gray-800 rounded-xl shadow-lg p-6">
            <Dialog.Title className="text-lg font-medium text-gray-900 dark:text-white mb-4">
              Select Your Avatar
            </Dialog.Title>

            <div className="grid grid-cols-5 md:grid-cols-8 gap-4 max-h-[60vh] overflow-y-auto p-2">
              {avatars.map((avatar) => (
                <button
                  key={avatar.id}
                  onClick={() => {
                    onSelect(avatar.url);
                    setIsOpen(false);
                  }}
                  className={`relative aspect-square rounded-lg transition-all duration-200 overflow-hidden ${
                    selectedUrl === avatar.url
                      ? 'ring-2 ring-primary-500 bg-primary-50 dark:bg-primary-900'
                      : 'hover:bg-gray-50 dark:hover:bg-gray-700'
                  }`}
                >
                  <img
                    src={avatar.url}
                    alt={avatar.alt}
                    className="w-full h-full object-contain p-2"
                  />
                </button>
              ))}
            </div>

            <div className="mt-6 flex justify-end">
              <button
                onClick={() => setIsOpen(false)}
                className="px-4 py-2 text-sm font-medium text-gray-700 bg-white border border-gray-300 rounded-md hover:bg-gray-50 dark:bg-gray-700 dark:text-gray-300 dark:border-gray-600 dark:hover:bg-gray-600"
              >
                Cancel
              </button>
            </div>
          </Dialog.Panel>
        </div>
      </Dialog>
    </>
  );
};

export default AvatarSelection;
