import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import {
  CalendarIcon,
  PlusIcon,
  CheckCircleIcon,
  XCircleIcon,
} from '@heroicons/react/24/outline';
import { useAuth } from '../../context/AuthContext';
import { tasks } from '../../api/client';

interface Task {
  id: string;
  title: string;
  description?: string;
  dueDate: string;
  status: 'PENDING' | 'COMPLETED' | 'OVERDUE';
  assignedTo: {
    id: string;
    username: string;
  };
}

interface TasksResponse {
  data: Task[];
}

const Chores: React.FC = () => {
  const { user } = useAuth();
  const [householdTasks, setHouseholdTasks] = useState<Task[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [showAddModal, setShowAddModal] = useState(false);

  useEffect(() => {
    const fetchTasks = async () => {
      if (!user?.householdId) return;

      try {
        setLoading(true);
        setError(null);
        const response = await tasks.getHouseholdTasks(user.householdId);
        setHouseholdTasks((response as TasksResponse).data);
      } catch (error: any) {
        setError(error.response?.data?.error || 'Failed to load tasks');
      } finally {
        setLoading(false);
      }
    };

    fetchTasks();
  }, [user?.householdId]);

  const handleStatusUpdate = async (taskId: string, status: 'PENDING' | 'COMPLETED' | 'OVERDUE') => {
    try {
      await tasks.updateStatus(taskId, { status });
      setHouseholdTasks((prevTasks) =>
        prevTasks.map((task) =>
          task.id === taskId ? { ...task, status } : task
        )
      );
    } catch (error: any) {
      setError(error.response?.data?.error || 'Failed to update task status');
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary-500"></div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="text-red-500">{error}</div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
      >
        <div className="flex items-center justify-between">
          <h1 className="text-3xl font-bold text-gray-900 dark:text-white">Chores</h1>
          <button
            onClick={() => setShowAddModal(true)}
            className="flex items-center px-4 py-2 text-sm font-medium text-white bg-primary-600 rounded-md hover:bg-primary-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500 dark:focus:ring-offset-gray-900"
          >
            <PlusIcon className="w-5 h-5 mr-2" />
            Add Chore
          </button>
        </div>
      </motion.div>

      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5, delay: 0.1 }}
        className="card"
      >
        <h2 className="text-xl font-semibold text-gray-900 dark:text-white mb-4">Current Chores</h2>
        <div className="space-y-4">
          {householdTasks.map((task) => (
            <div
              key={task.id}
              className="flex items-center justify-between p-4 bg-gray-50 dark:bg-gray-700 rounded-lg transition-colors duration-200"
            >
              <div>
                <h3 className="font-medium text-gray-900 dark:text-white">{task.title}</h3>
                <p className="text-sm text-gray-500 dark:text-gray-400">
                  Assigned to {task.assignedTo.username}
                </p>
                {task.description && (
                  <p className="text-sm text-gray-500 dark:text-gray-400 mt-1">{task.description}</p>
                )}
                <p className="text-sm text-gray-500 dark:text-gray-400 mt-1">
                  Due: {new Date(task.dueDate).toLocaleDateString()}
                </p>
              </div>
              <div className="flex items-center space-x-4">
                <button
                  onClick={() => handleStatusUpdate(task.id, 'COMPLETED')}
                  className={`p-2 rounded-full ${
                    task.status === 'COMPLETED'
                      ? 'text-primary-500 dark:text-primary-400'
                      : 'text-gray-400 hover:text-primary-500 dark:hover:text-primary-400'
                  }`}
                >
                  <CheckCircleIcon className="w-6 h-6" />
                </button>
                <button
                  onClick={() => handleStatusUpdate(task.id, 'PENDING')}
                  className={`p-2 rounded-full ${
                    task.status === 'PENDING'
                      ? 'text-primary-500 dark:text-primary-400'
                      : 'text-gray-400 hover:text-primary-500 dark:hover:text-primary-400'
                  }`}
                >
                  <XCircleIcon className="w-6 h-6" />
                </button>
              </div>
            </div>
          ))}
        </div>
      </motion.div>

      {/* Add Chore Modal - This would be a separate component in a real app */}
      {showAddModal && (
        <div className="fixed inset-0 z-10 overflow-y-auto">
          <div className="flex items-center justify-center min-h-screen px-4 pt-4 pb-20 text-center sm:block sm:p-0">
            <div className="fixed inset-0 transition-opacity" aria-hidden="true">
              <div className="absolute inset-0 bg-gray-500 dark:bg-gray-900 opacity-75"></div>
            </div>
            <span className="hidden sm:inline-block sm:align-middle sm:h-screen" aria-hidden="true">
              &#8203;
            </span>
            <div className="inline-block align-bottom bg-white dark:bg-gray-800 rounded-lg text-left overflow-hidden shadow-xl transform transition-all sm:my-8 sm:align-middle sm:max-w-lg sm:w-full">
              {/* Modal content would go here */}
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Chores;
