import React, { useEffect, useState } from 'react';
import { motion } from 'framer-motion';
import { useNavigate } from 'react-router-dom';
import { household } from '../api/client';
import toast from 'react-hot-toast';
import { useAuth } from '../context/AuthContext';

interface Group {
  id: string;
  name: string;
  code: string;
  isPrivate: boolean;
  members: {
    id: string;
    username: string;
  }[];
}

interface HouseholdResponse {
  id: string;
  name: string;
  code: string;
  isPrivate: boolean;
}

const FindGroups = () => {
  const [groups, setGroups] = useState<Group[]>([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();
  const { user, updateUser } = useAuth();

  useEffect(() => {
    fetchGroups();
  }, []);

  const fetchGroups = async () => {
    try {
      const response = await household.getAll();
      setGroups(response.data as Group[]);
    } catch (error) {
      toast.error('Failed to fetch groups');
    } finally {
      setLoading(false);
    }
  };

  const handleJoinGroup = async (code: string) => {
    try {
      const response = await household.join(code);
      const householdData = response.data as HouseholdResponse;

      // Update the user's householdId in the auth context
      if (user) {
        const updatedUser = { ...user, householdId: householdData.id };
        updateUser(updatedUser);
      }

      toast.success('Successfully joined the group!');
      navigate('/dashboard');
    } catch (error) {
      toast.error('Failed to join group');
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-primary-500"></div>
      </div>
    );
  }

  return (
    <div className="container mx-auto px-4 py-8">
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
      >
        <h1 className="text-3xl font-bold mb-8">Available Groups</h1>

        {groups.length === 0 ? (
          <div className="text-center py-12">
            <p className="text-gray-600 dark:text-gray-400">No groups available yet.</p>
          </div>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {groups.map((group) => (
              <motion.div
                key={group.id}
                className="bg-white dark:bg-gray-800 rounded-lg shadow-md overflow-hidden"
                whileHover={{ scale: 1.02 }}
                transition={{ type: "spring", stiffness: 300 }}
              >
                <div className="p-6">
                  <div className="flex items-center justify-between mb-4">
                    <h2 className="text-xl font-semibold">{group.name}</h2>
                    <span className={`px-3 py-1 rounded-full text-sm ${
                      group.isPrivate 
                        ? 'bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-200'
                        : 'bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200'
                    }`}>
                      {group.isPrivate ? 'Private' : 'Public'}
                    </span>
                  </div>

                  <div className="mb-4">
                    <h3 className="text-sm font-medium text-gray-500 dark:text-gray-400 mb-2">Members:</h3>
                    <div className="flex flex-wrap gap-2">
                      {group.members.map((member) => (
                        <span
                          key={member.id}
                          className="px-2 py-1 bg-gray-100 dark:bg-gray-700 rounded-md text-sm"
                        >
                          {member.username}
                        </span>
                      ))}
                    </div>
                  </div>

                  <button
                    onClick={() => handleJoinGroup(group.code)}
                    className="w-full px-4 py-2 bg-primary-500 text-white rounded-lg hover:bg-primary-600 transition-colors duration-200"
                    disabled={group.isPrivate}
                  >
                    {group.isPrivate ? 'Private Group' : 'Join Group'}
                  </button>
                </div>
              </motion.div>
            ))}
          </div>
        )}
      </motion.div>
    </div>
  );
};

export default FindGroups;
