import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { 
  EnvelopeIcon, 
  ExclamationCircleIcon, 
  CheckCircleIcon,
  XCircleIcon,
  ClockIcon
} from '@heroicons/react/24/outline';
import { useAuth } from '../../context/AuthContext';
import { api } from '../../services/api';

interface Message {
  id: string;
  type: 'invitation' | 'join_request' | 'system';
  title: string;
  content: string;
  createdAt: string;
  read: boolean;
  status?: 'pending' | 'accepted' | 'rejected';
  senderId?: string;
  senderName?: string;
  householdId?: string;
  householdName?: string;
}

const Inbox: React.FC = () => {
  const { user } = useAuth();
  const [messages, setMessages] = useState<Message[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [successMessage, setSuccessMessage] = useState<string | null>(null);
  const [processingMessageId, setProcessingMessageId] = useState<string | null>(null);

  useEffect(() => {
    const fetchMessages = async () => {
      try {
        const response = await api.get<Message[]>('/messages');
        setMessages(response.data);
      } catch (err) {
        console.error('Error fetching messages:', err);
        setError('Failed to load messages. Please try again later.');
      } finally {
        setLoading(false);
      }
    };

    fetchMessages();
  }, []);

  const handleMarkAsRead = async (messageId: string) => {
    try {
      await api.put(`/messages/${messageId}/read`);
      setMessages(prevMessages => 
        prevMessages.map(msg => 
          msg.id === messageId ? { ...msg, read: true } : msg
        )
      );
    } catch (err) {
      console.error('Error marking message as read:', err);
      setError('Failed to mark message as read. Please try again.');
    }
  };

  const handleAcceptInvitation = async (messageId: string, householdId: string) => {
    setProcessingMessageId(messageId);
    setError(null);
    setSuccessMessage(null);

    try {
      await api.post(`/invitations/${messageId}/accept`);
      setSuccessMessage('Invitation accepted successfully!');

      // Update the message status
      setMessages(prevMessages => 
        prevMessages.map(msg => 
          msg.id === messageId ? { ...msg, status: 'accepted' } : msg
        )
      );
    } catch (err) {
      console.error('Error accepting invitation:', err);
      setError('Failed to accept invitation. Please try again later.');
    } finally {
      setProcessingMessageId(null);
    }
  };

  const handleRejectInvitation = async (messageId: string) => {
    setProcessingMessageId(messageId);
    setError(null);
    setSuccessMessage(null);

    try {
      await api.post(`/invitations/${messageId}/reject`);
      setSuccessMessage('Invitation rejected.');

      // Update the message status
      setMessages(prevMessages => 
        prevMessages.map(msg => 
          msg.id === messageId ? { ...msg, status: 'rejected' } : msg
        )
      );
    } catch (err) {
      console.error('Error rejecting invitation:', err);
      setError('Failed to reject invitation. Please try again later.');
    } finally {
      setProcessingMessageId(null);
    }
  };

  const handleAcceptJoinRequest = async (messageId: string, userId: string) => {
    setProcessingMessageId(messageId);
    setError(null);
    setSuccessMessage(null);

    try {
      await api.post(`/join-requests/${messageId}/accept`);
      setSuccessMessage('Join request accepted successfully!');

      // Update the message status
      setMessages(prevMessages => 
        prevMessages.map(msg => 
          msg.id === messageId ? { ...msg, status: 'accepted' } : msg
        )
      );
    } catch (err) {
      console.error('Error accepting join request:', err);
      setError('Failed to accept join request. Please try again later.');
    } finally {
      setProcessingMessageId(null);
    }
  };

  const handleRejectJoinRequest = async (messageId: string) => {
    setProcessingMessageId(messageId);
    setError(null);
    setSuccessMessage(null);

    try {
      await api.post(`/join-requests/${messageId}/reject`);
      setSuccessMessage('Join request rejected.');

      // Update the message status
      setMessages(prevMessages => 
        prevMessages.map(msg => 
          msg.id === messageId ? { ...msg, status: 'rejected' } : msg
        )
      );
    } catch (err) {
      console.error('Error rejecting join request:', err);
      setError('Failed to reject join request. Please try again later.');
    } finally {
      setProcessingMessageId(null);
    }
  };

  const formatDate = (dateString: string) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('en-US', { 
      year: 'numeric', 
      month: 'short', 
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const getStatusIcon = (status?: string) => {
    switch (status) {
      case 'accepted':
        return <CheckCircleIcon className="h-5 w-5 text-green-500" />;
      case 'rejected':
        return <XCircleIcon className="h-5 w-5 text-red-500" />;
      case 'pending':
        return <ClockIcon className="h-5 w-5 text-yellow-500" />;
      default:
        return null;
    }
  };

  return (
    <div className="container mx-auto px-4 py-8">
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
      >
        <h1 className="text-3xl font-bold text-gray-900 mb-8">Inbox</h1>

        {/* Error Message */}
        {error && (
          <div className="bg-red-50 border-l-4 border-red-400 p-4 mb-6 flex items-start">
            <ExclamationCircleIcon className="h-5 w-5 text-red-400 mr-2 mt-0.5" />
            <p className="text-red-700">{error}</p>
          </div>
        )}

        {/* Success Message */}
        {successMessage && (
          <div className="bg-green-50 border-l-4 border-green-400 p-4 mb-6">
            <p className="text-green-700">{successMessage}</p>
          </div>
        )}

        {/* Loading State */}
        {loading && (
          <div className="flex justify-center items-center py-8">
            <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-500"></div>
          </div>
        )}

        {/* Messages */}
        {!loading && messages.length > 0 && (
          <div className="space-y-4">
            {messages.map((message) => (
              <motion.div
                key={message.id}
                initial={{ opacity: 0, y: 10 }}
                animate={{ opacity: 1, y: 0 }}
                className={`bg-white rounded-lg shadow-md p-6 ${!message.read ? 'border-l-4 border-blue-500' : ''}`}
              >
                <div className="flex flex-col">
                  <div className="flex items-start justify-between mb-4">
                    <div className="flex items-center">
                      <EnvelopeIcon className={`h-6 w-6 mr-3 ${!message.read ? 'text-blue-500' : 'text-gray-400'}`} />
                      <div>
                        <h3 className="text-lg font-semibold text-gray-900">{message.title}</h3>
                        <p className="text-sm text-gray-500">
                          {message.type === 'invitation' && `From: ${message.senderName}`}
                          {message.type === 'join_request' && `From: ${message.senderName}`}
                          {message.type === 'system' && 'System Message'}
                        </p>
                      </div>
                    </div>
                    <div className="flex items-center">
                      {getStatusIcon(message.status)}
                      <span className="text-sm text-gray-500 ml-2">{formatDate(message.createdAt)}</span>
                    </div>
                  </div>

                  <p className="text-gray-600 mb-4">{message.content}</p>

                  <div className="flex items-center justify-between mt-4">
                    {!message.read && (
                      <button
                        onClick={() => handleMarkAsRead(message.id)}
                        className="text-sm text-blue-500 hover:text-blue-600"
                      >
                        Mark as read
                      </button>
                    )}

                    <div className="flex space-x-2">
                      {message.type === 'invitation' && message.status !== 'accepted' && message.status !== 'rejected' && (
                        <>
                          <button
                            onClick={() => handleAcceptInvitation(message.id, message.householdId!)}
                            disabled={processingMessageId === message.id}
                            className={`px-4 py-2 rounded-lg transition-colors ${
                              processingMessageId === message.id
                                ? 'bg-gray-300 cursor-not-allowed'
                                : 'bg-green-500 hover:bg-green-600 text-white'
                            }`}
                          >
                            {processingMessageId === message.id ? 'Processing...' : 'Accept'}
                          </button>
                          <button
                            onClick={() => handleRejectInvitation(message.id)}
                            disabled={processingMessageId === message.id}
                            className={`px-4 py-2 rounded-lg transition-colors ${
                              processingMessageId === message.id
                                ? 'bg-gray-300 cursor-not-allowed'
                                : 'bg-red-500 hover:bg-red-600 text-white'
                            }`}
                          >
                            {processingMessageId === message.id ? 'Processing...' : 'Reject'}
                          </button>
                        </>
                      )}

                      {message.type === 'join_request' && message.status !== 'accepted' && message.status !== 'rejected' && (
                        <>
                          <button
                            onClick={() => handleAcceptJoinRequest(message.id, message.senderId!)}
                            disabled={processingMessageId === message.id}
                            className={`px-4 py-2 rounded-lg transition-colors ${
                              processingMessageId === message.id
                                ? 'bg-gray-300 cursor-not-allowed'
                                : 'bg-green-500 hover:bg-green-600 text-white'
                            }`}
                          >
                            {processingMessageId === message.id ? 'Processing...' : 'Accept'}
                          </button>
                          <button
                            onClick={() => handleRejectJoinRequest(message.id)}
                            disabled={processingMessageId === message.id}
                            className={`px-4 py-2 rounded-lg transition-colors ${
                              processingMessageId === message.id
                                ? 'bg-gray-300 cursor-not-allowed'
                                : 'bg-red-500 hover:bg-red-600 text-white'
                            }`}
                          >
                            {processingMessageId === message.id ? 'Processing...' : 'Reject'}
                          </button>
                        </>
                      )}
                    </div>
                  </div>
                </div>
              </motion.div>
            ))}
          </div>
        )}

        {/* No Messages */}
        {!loading && messages.length === 0 && (
          <div className="text-center py-12 bg-gray-50 rounded-lg">
            <EnvelopeIcon className="h-12 w-12 text-gray-400 mx-auto mb-4" />
            <h3 className="text-lg font-medium text-gray-900 mb-2">No Messages</h3>
            <p className="text-gray-500 max-w-md mx-auto">
              You don't have any messages yet. Check back later for invitations and join requests.
            </p>
          </div>
        )}
      </motion.div>
    </div>
  );
};

export default Inbox;
