import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import {
  CalendarIcon,
  PlusIcon,
  CheckCircleIcon,
  XCircleIcon,
} from '@heroicons/react/24/outline';
import apiClient from '../../api/client';

interface Task {
  id: string;
  name: string;
  frequency: string;
  assignedTo?: string;
  completed?: boolean;
}

interface User {
  id: string;
  username: string;
}

const Chores: React.FC = () => {
  const [tasks, setTasks] = useState<Task[]>([]);
  const [roommates, setRoommates] = useState<User[]>([]);
  const [selectedTask, setSelectedTask] = useState<string | null>(null);
  const [showAddModal, setShowAddModal] = useState(false);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const fetchChoresData = async () => {
      try {
        setIsLoading(true);
        // Fetch tasks and roommates from your API
        // const tasksResponse = await apiClient.get('/api/tasks');
        // const roommatesResponse = await apiClient.get('/api/household/members');
        // setTasks(tasksResponse.data);
        // setRoommates(roommatesResponse.data);
      } catch (error) {
        console.error('Error fetching chores data:', error);
      } finally {
        setIsLoading(false);
      }
    };

    fetchChoresData();
  }, []);

  const daysOfWeek = ['Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat', 'Sun'];

  return (
    <div className="space-y-6">
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
        className="flex justify-between items-center"
      >
        <div>
          <h1 className="text-3xl font-bold text-neutral-900">Chores</h1>
          <p className="text-neutral-500 mt-2">Manage and track household chores</p>
        </div>
        <button
          onClick={() => setShowAddModal(true)}
          className="btn btn-primary flex items-center space-x-2"
        >
          <PlusIcon className="w-5 h-5" />
          <span>Add Chore</span>
        </button>
      </motion.div>

      {isLoading ? (
        <div className="text-center py-8">Loading chores...</div>
      ) : tasks.length === 0 ? (
        <div className="text-center py-8 text-neutral-500">
          No chores added yet. Click the "Add Chore" button to get started.
        </div>
      ) : (
        <div className="grid gap-6">
          {tasks.map((task) => (
            <motion.div
              key={task.id}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              className="card"
            >
              <div className="flex items-center justify-between">
                <div>
                  <h3 className="text-lg font-medium text-neutral-900">{task.name}</h3>
                  <p className="text-sm text-neutral-500">Frequency: {task.frequency}</p>
                  {task.assignedTo && (
                    <p className="text-sm text-neutral-500">Assigned to: {task.assignedTo}</p>
                  )}
                </div>
                <div className="flex items-center space-x-2">
                  {task.completed ? (
                    <CheckCircleIcon className="w-6 h-6 text-green-500" />
                  ) : (
                    <XCircleIcon className="w-6 h-6 text-neutral-300" />
                  )}
                </div>
              </div>
            </motion.div>
          ))}
        </div>
      )}

      {/* Add Chore Modal would go here */}
    </div>
  );
};

export default Chores;
