import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { MagnifyingGlassIcon, UserGroupIcon, ExclamationCircleIcon } from '@heroicons/react/24/outline';
import { useAuth } from '../../context/AuthContext';
import { api } from '../../services/api';

interface Household {
  id: string;
  name: string;
  description: string;
  memberCount: number;
  ownerId: string;
  ownerName: string;
  createdAt: string;
  isPrivate: boolean;
}

const FindGroups: React.FC = () => {
  const { user } = useAuth();
  const [searchQuery, setSearchQuery] = useState('');
  const [households, setHouseholds] = useState<Household[]>([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [successMessage, setSuccessMessage] = useState<string | null>(null);
  const [requestingHouseholdId, setRequestingHouseholdId] = useState<string | null>(null);

  useEffect(() => {
    const searchHouseholds = async () => {
      if (!searchQuery.trim()) {
        setHouseholds([]);
        return;
      }

      setLoading(true);
      setError(null);

      try {
        const response = await api.get<Household[]>(`/households/search?q=${encodeURIComponent(searchQuery)}`);
        // Filter out the user's current household
        const filteredHouseholds = response.data.filter(h => h.id !== user?.householdId);
        setHouseholds(filteredHouseholds);
      } catch (err) {
        console.error('Error searching groups:', err);
        setError('Failed to search groups. Please try again later.');
      } finally {
        setLoading(false);
      }
    };

    const debounceTimer = setTimeout(searchHouseholds, 300);
    return () => clearTimeout(debounceTimer);
  }, [searchQuery, user?.householdId]);

  const handleJoinRequest = async (householdId: string) => {
    if (!user?.id) {
      setError('You must be logged in to join a household.');
      return;
    }

    setRequestingHouseholdId(householdId);
    setError(null);
    setSuccessMessage(null);

    try {
      await api.post('/join-requests', {
        householdId,
        userId: user.id,
      });
      setSuccessMessage('Join request sent successfully!');

      // Update the UI to show the request has been sent
      setHouseholds(prevHouseholds => 
        prevHouseholds.map(h => 
          h.id === householdId ? { ...h, requested: true } : h
        )
      );
    } catch (err: any) {
      console.error('Error sending join request:', err);
      if (err.response?.status === 409) {
        setError('You have already requested to join this household.');
      } else if (err.response?.status === 403) {
        setError('You do not have permission to join this household.');
      } else {
        setError('Failed to send join request. Please try again later.');
      }
    } finally {
      setRequestingHouseholdId(null);
    }
  };

  const formatDate = (dateString: string) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('en-US', { 
      year: 'numeric', 
      month: 'short', 
      day: 'numeric' 
    });
  };

  return (
    <div className="container mx-auto px-4 py-8">
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
      >
        <h1 className="text-3xl font-bold text-gray-900 mb-8">Find Groups</h1>

        {/* Search Bar */}
        <div className="relative mb-8">
          <div className="relative">
            <input
              type="text"
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              placeholder="Search by group name or description..."
              className="w-full px-4 py-3 pl-12 rounded-lg border border-gray-300 focus:ring-2 focus:ring-blue-500 focus:border-transparent"
            />
            <MagnifyingGlassIcon className="h-6 w-6 text-gray-400 absolute left-3 top-1/2 transform -translate-y-1/2" />
          </div>
        </div>

        {/* Error Message */}
        {error && (
          <div className="bg-red-50 border-l-4 border-red-400 p-4 mb-6 flex items-start">
            <ExclamationCircleIcon className="h-5 w-5 text-red-400 mr-2 mt-0.5" />
            <p className="text-red-700">{error}</p>
          </div>
        )}

        {/* Success Message */}
        {successMessage && (
          <div className="bg-green-50 border-l-4 border-green-400 p-4 mb-6">
            <p className="text-green-700">{successMessage}</p>
          </div>
        )}

        {/* Loading State */}
        {loading && (
          <div className="flex justify-center items-center py-8">
            <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-500"></div>
          </div>
        )}

        {/* Results */}
        {!loading && households.length > 0 && (
          <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-3">
            {households.map((household) => (
              <motion.div
                key={household.id}
                initial={{ opacity: 0, scale: 0.95 }}
                animate={{ opacity: 1, scale: 1 }}
                className="bg-white rounded-lg shadow-md p-6"
              >
                <div className="flex flex-col h-full">
                  <div className="flex items-center mb-4">
                    <div className="h-12 w-12 rounded-full bg-blue-100 flex items-center justify-center mr-3">
                      <UserGroupIcon className="h-6 w-6 text-blue-500" />
                    </div>
                    <div>
                      <h3 className="text-lg font-semibold text-gray-900">{household.name}</h3>
                      <p className="text-sm text-gray-500">Created by {household.ownerName}</p>
                    </div>
                  </div>
                  <p className="text-gray-600 mb-4 flex-grow">{household.description}</p>
                  <div className="flex flex-col space-y-2">
                    <div className="flex items-center justify-between">
                      <span className="text-sm text-gray-500">
                        {household.memberCount} members
                      </span>
                      <span className="text-sm text-gray-500">
                        Created {formatDate(household.createdAt)}
                      </span>
                    </div>
                    {household.isPrivate && (
                      <span className="text-xs bg-gray-100 text-gray-600 px-2 py-1 rounded-full inline-block">
                        Private Group
                      </span>
                    )}
                    {user?.householdId !== household.id && (
                      <button
                        onClick={() => handleJoinRequest(household.id)}
                        disabled={requestingHouseholdId === household.id || household.requested}
                        className={`w-full px-4 py-2 rounded-lg transition-colors ${
                          requestingHouseholdId === household.id
                            ? 'bg-gray-300 cursor-not-allowed'
                            : household.requested
                              ? 'bg-green-500 text-white cursor-not-allowed'
                              : 'bg-blue-500 hover:bg-blue-600 text-white'
                        }`}
                      >
                        {requestingHouseholdId === household.id ? (
                          <span className="flex items-center justify-center">
                            <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                            Requesting...
                          </span>
                        ) : household.requested ? (
                          'Request Sent'
                        ) : (
                          'Request to Join'
                        )}
                      </button>
                    )}
                  </div>
                </div>
              </motion.div>
            ))}
          </div>
        )}

        {/* No Results */}
        {!loading && searchQuery && households.length === 0 && (
          <div className="text-center py-8">
            <p className="text-gray-500">No groups found matching your search.</p>
          </div>
        )}

        {/* Empty State */}
        {!loading && !searchQuery && (
          <div className="text-center py-12 bg-gray-50 rounded-lg">
            <UserGroupIcon className="h-12 w-12 text-gray-400 mx-auto mb-4" />
            <h3 className="text-lg font-medium text-gray-900 mb-2">Find Households</h3>
            <p className="text-gray-500 max-w-md mx-auto">
              Search for households by name or description to find potential roommates.
            </p>
          </div>
        )}
      </motion.div>
    </div>
  );
};

export default FindGroups;
