import React, { createContext, useContext, useState, useEffect } from 'react';
import { auth } from '../api/client';
import { useNavigate } from 'react-router-dom';
import { User } from '../types/user';

interface AuthResponse {
  token: string;
  user: User;
}

interface AuthContextType {
  user: User | null;
  token: string | null;
  error: string | null;
  isAuthenticated: boolean;
  isLoading: boolean;
  login: (email: string, password: string) => Promise<void>;
  register: (username: string, email: string, password: string) => Promise<void>;
  logout: () => void;
  updateUser: (updatedUser: User) => void;
  signup: (email: string, password: string, name: string) => Promise<void>;
  refreshUserGroupState: () => Promise<void>;
}

const AuthContext = createContext<AuthContextType | undefined>(undefined);

export const AuthProvider: React.FC<{ children: React.ReactNode }> = ({ children }) => {
  const [user, setUser] = useState<User | null>(null);
  const [token, setToken] = useState<string | null>(null);
  const [error, setError] = useState<string | null>(null);
  const [isLoading, setIsLoading] = useState(true);
  const isAuthenticated = !!token;
  const navigate = useNavigate();

  useEffect(() => {
    const initializeAuth = async () => {
      try {
        const storedToken = localStorage.getItem('token');

        if (storedToken) {
          const response = await auth.getProfile();
          const userData = response.data as User;

          // Always use the householdId from the backend response
          // This ensures we have the most up-to-date group membership state
          setUser(userData);
          setToken(storedToken);

          // Update localStorage with the latest householdId
          if (userData.householdId) {
            localStorage.setItem('householdId', userData.householdId);
          } else {
            localStorage.removeItem('householdId');
          }
        }
      } catch (error) {
        console.error('Failed to initialize auth:', error);
        // Clear invalid token and householdId
        localStorage.removeItem('token');
        localStorage.removeItem('householdId');
      } finally {
        setIsLoading(false);
      }
    };

    initializeAuth();
  }, []);

  // Add a function to refresh user's group membership state
  const refreshUserGroupState = async () => {
    try {
      const response = await auth.getProfile();
      const userData = response.data as User;
      setUser(userData);

      // Update localStorage with the latest householdId
      if (userData.householdId) {
        localStorage.setItem('householdId', userData.householdId);
      } else {
        localStorage.removeItem('householdId');
      }
    } catch (error) {
      console.error('Failed to refresh user group state:', error);
    }
  };

  const login = async (email: string, password: string) => {
    try {
      setIsLoading(true);
      setError(null);
      const response = await auth.login(email, password);
      const { token, user } = response.data as AuthResponse;

      localStorage.setItem('token', token);
      setToken(token);
      setUser(user);

      if (user.householdId) {
        localStorage.setItem('householdId', user.householdId);
      }

      navigate('/');
    } catch (error) {
      setError('Invalid email or password');
      throw error;
    } finally {
      setIsLoading(false);
    }
  };

  const register = async (username: string, email: string, password: string) => {
    try {
      setError(null);
      const response = await auth.register(username, email, password);
      const { token, user } = response.data as AuthResponse;

      // Set token first
      setToken(token);
      localStorage.setItem('token', token);

      // Then set user
      setUser(user);

      // Save householdId if it exists
      if (user.householdId) {
        localStorage.setItem('householdId', user.householdId);
      }

      // Navigate to dashboard after successful registration
      navigate('/');

      return Promise.resolve();
    } catch (error: any) {
      const errorMessage = error.response?.data?.error || 'Registration failed. Please try again.';
      setError(errorMessage);
      console.error('Registration error:', error);
      return Promise.reject(error);
    }
  };

  const logout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('householdId');
    setToken(null);
    setUser(null);
    navigate('/login');
  };

  const updateUser = (updatedUser: User) => {
    setUser(updatedUser);

    // Update householdId in localStorage if it changed
    if (updatedUser.householdId) {
      localStorage.setItem('householdId', updatedUser.householdId);
    } else {
      localStorage.removeItem('householdId');
    }
  };

  const signup = async (email: string, password: string, name: string) => {
    // Implementation for signup method
  };

  const value = {
    user,
    token,
    error,
    isAuthenticated,
    isLoading,
    login,
    register,
    logout,
    updateUser,
    signup,
    refreshUserGroupState
  };

  if (isLoading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-blue-900 via-indigo-900 to-purple-900">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-white"></div>
      </div>
    );
  }

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (context === undefined) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};
