import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import {
  CalendarIcon,
  PlusIcon,
  CheckCircleIcon,
  XCircleIcon,
  UserGroupIcon,
} from '@heroicons/react/24/outline';
import { tasks } from '../../api/client';

interface Task {
  id: string;
  name: string;
  frequency: string;
  assignedTo?: string;
  completed?: boolean;
}

interface User {
  id: string;
  name: string;
}

const Chores: React.FC = () => {
  const [tasksList, setTasks] = useState<Task[]>([]);
  const [roommates, setRoommates] = useState<User[]>([]);
  const [selectedTask, setSelectedTask] = useState<string | null>(null);
  const [showAddModal, setShowAddModal] = useState(false);
  const [showAddAllModal, setShowAddAllModal] = useState(false);
  const [isLoading, setIsLoading] = useState(true);
  const [newTask, setNewTask] = useState({
    title: '',
    description: '',
    dueDate: '',
  });

  useEffect(() => {
    const fetchChoresData = async () => {
      try {
        setIsLoading(true);
        // Fetch tasks and roommates from your API
        // const tasksResponse = await apiClient.get('/api/tasks');
        // const roommatesResponse = await apiClient.get('/api/household/members');
        // setTasks(tasksResponse.data);
        // setRoommates(roommatesResponse.data);
      } catch (error) {
        console.error('Error fetching chores data:', error);
      } finally {
        setIsLoading(false);
      }
    };

    fetchChoresData();
  }, []);

  const handleCreateTaskForAll = async (householdId: string) => {
    try {
      await tasks.createForAllMembers(householdId, newTask);
      setShowAddAllModal(false);
      setNewTask({ title: '', description: '', dueDate: '' });
      // Refresh tasks list
      const response = await tasks.getHouseholdTasks(householdId);
      if (response && response.data) {
        setTasks(response.data as Task[]);
      }
    } catch (error) {
      console.error('Error creating tasks for all members:', error);
    }
  };

  const daysOfWeek = ['Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat', 'Sun'];

  return (
    <div className="space-y-6">
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
        className="flex justify-between items-center"
      >
        <div>
          <h1 className="text-3xl font-bold text-neutral-900">Chores</h1>
          <p className="text-neutral-500 mt-2">Manage and track household chores</p>
        </div>
        <div className="flex space-x-4">
          <button
            onClick={() => setShowAddAllModal(true)}
            className="flex items-center px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700"
          >
            <UserGroupIcon className="w-5 h-5 mr-2" />
            Add for All
          </button>
          <button
            onClick={() => setShowAddModal(true)}
            className="flex items-center px-4 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700"
          >
            <PlusIcon className="w-5 h-5 mr-2" />
            Add Chore
          </button>
        </div>
      </motion.div>

      {isLoading ? (
        <div className="text-center py-8">Loading chores...</div>
      ) : tasksList.length === 0 ? (
        <div className="text-center py-8 text-neutral-500">
          No chores added yet. Click the "Add Chore" button to get started.
        </div>
      ) : (
        <div className="grid gap-6">
          {tasksList.map((task) => (
            <motion.div
              key={task.id}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              className="card"
            >
              <div className="flex items-center justify-between">
                <div>
                  <h3 className="text-lg font-medium text-neutral-900">{task.name}</h3>
                  <p className="text-sm text-neutral-500">Frequency: {task.frequency}</p>
                  {task.assignedTo && (
                    <p className="text-sm text-neutral-500">Assigned to: {task.assignedTo}</p>
                  )}
                </div>
                <div className="flex items-center space-x-2">
                  {task.completed ? (
                    <CheckCircleIcon className="w-6 h-6 text-green-500" />
                  ) : (
                    <XCircleIcon className="w-6 h-6 text-neutral-300" />
                  )}
                </div>
              </div>
            </motion.div>
          ))}
        </div>
      )}

      {/* Add for All Modal */}
      {showAddAllModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center">
          <div className="bg-white p-6 rounded-lg w-96">
            <h2 className="text-xl font-bold mb-4">Add Chore for All Members</h2>
            <div className="space-y-4">
              <input
                type="text"
                placeholder="Chore Title"
                className="w-full p-2 border rounded"
                value={newTask.title}
                onChange={(e) => setNewTask({ ...newTask, title: e.target.value })}
              />
              <textarea
                placeholder="Description"
                className="w-full p-2 border rounded"
                value={newTask.description}
                onChange={(e) => setNewTask({ ...newTask, description: e.target.value })}
              />
              <input
                type="date"
                className="w-full p-2 border rounded"
                value={newTask.dueDate}
                onChange={(e) => setNewTask({ ...newTask, dueDate: e.target.value })}
              />
              <div className="flex justify-end space-x-2">
                <button
                  onClick={() => setShowAddAllModal(false)}
                  className="px-4 py-2 bg-gray-200 rounded"
                >
                  Cancel
                </button>
                <button
                  onClick={() => handleCreateTaskForAll('current-household-id')}
                  className="px-4 py-2 bg-blue-600 text-white rounded"
                >
                  Create
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Chores;
