import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import { household } from '../../api/client';
import toast from 'react-hot-toast';

const CreateHousehold: React.FC = () => {
  const [name, setName] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();
  const { user, updateUser } = useAuth();

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);

    try {
      if (!name.trim()) {
        toast.error('Please enter a household name');
        return;
      }

      const householdData = await household.create({ name: name.trim() });

      // Update user's household ID in auth context
      const updatedUser = { ...user, householdId: householdData.id };
      updateUser(updatedUser);

      toast.success('Household created successfully!');
      navigate('/dashboard');
    } catch (error: any) {
      if (error.response?.data?.error === 'You are already a member of a household') {
        toast.error('You are already a member of a household. Please leave your current household first.');
        navigate('/profile');
      } else {
        toast.error(error.response?.data?.error || 'Error creating household');
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-md mx-auto bg-white rounded-lg shadow-md p-6">
        <div className="mb-6">
          <button
            onClick={() => navigate(-1)}
            className="text-gray-600 hover:text-gray-900"
          >
            ‚Üê Back
          </button>
        </div>
        <h2 className="text-2xl font-bold text-gray-900 mb-6">Create a New Household</h2>
        <form onSubmit={handleSubmit} className="space-y-6">
          <div>
            <label htmlFor="name" className="block text-sm font-medium text-gray-700">
              Household Name
            </label>
            <input
              type="text"
              id="name"
              value={name}
              onChange={(e) => setName(e.target.value)}
              className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
              placeholder="Enter household name"
              required
            />
            <p className="mt-1 text-sm text-gray-500">
              Choose a name for your household that all members will recognize.
            </p>
          </div>

          <button
            type="submit"
            disabled={loading}
            className="w-full flex justify-center py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 disabled:opacity-50"
          >
            {loading ? 'Creating...' : 'Create Household'}
          </button>
        </form>
      </div>
    </div>
  );
};

export default CreateHousehold;
