import React, { useEffect, useState } from 'react';
import Logo from '../common/Logo';
import { motion, useAnimation, useScroll, useTransform } from 'framer-motion';

interface AuthLayoutProps {
  children: React.ReactNode;
  title: string;
  subtitle?: string;
}

const AuthLayout: React.FC<AuthLayoutProps> = ({ children, title, subtitle }) => {
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });
  const controls = useAnimation();
  const { scrollY } = useScroll();
  const y = useTransform(scrollY, [0, 1000], [0, 200]);
  const opacity = useTransform(scrollY, [0, 300], [1, 0]);

  useEffect(() => {
    const handleMouseMove = (e: MouseEvent) => {
      setMousePosition({
        x: (e.clientX / window.innerWidth) * 2 - 1,
        y: (e.clientY / window.innerHeight) * 2 - 1,
      });
    };

    window.addEventListener('mousemove', handleMouseMove);
    return () => window.removeEventListener('mousemove', handleMouseMove);
  }, []);

  useEffect(() => {
    controls.start({
      scale: [1, 1.05, 1],
      rotate: [0, 5, 0],
      transition: {
        duration: 10,
        repeat: Infinity,
        repeatType: "reverse",
      },
    });
  }, [controls]);

  return (
    <div className="min-h-screen relative overflow-hidden bg-gradient-to-br from-slate-950 via-indigo-950 to-slate-950">
      {/* Animated background elements */}
      <div className="absolute inset-0 overflow-hidden">
        {/* Animated grid pattern */}
        <div className="absolute w-full h-full bg-[url('data:image/svg+xml;base64,')] opacity-10" />

        {/* Animated gradient orbs */}
        <motion.div
          className="absolute -top-1/2 -left-1/2 w-full h-full bg-gradient-to-br from-indigo-500/10 to-blue-500/10 rounded-full blur-3xl"
          animate={{
            scale: [1, 1.2, 1],
            rotate: [0, 90, 0],
          }}
          transition={{
            duration: 20,
            repeat: Infinity,
            ease: "linear"
          }}
        />
        <motion.div
          className="absolute -bottom-1/2 -right-1/2 w-full h-full bg-gradient-to-br from-purple-500/10 to-indigo-500/10 rounded-full blur-3xl"
          animate={{
            scale: [1.2, 1, 1.2],
            rotate: [90, 0, 90],
          }}
          transition={{
            duration: 20,
            repeat: Infinity,
            ease: "linear"
          }}
        />

        {/* Floating particles */}
        {[...Array(30)].map((_, i) => (
          <motion.div
            key={i}
            className="absolute w-1 h-1 bg-white/30 rounded-full"
            style={{
              left: `${Math.random() * 100}%`,
              top: `${Math.random() * 100}%`,
            }}
            animate={{
              y: [0, -20, 0],
              opacity: [0.3, 0.8, 0.3],
            }}
            transition={{
              duration: 3 + Math.random() * 5,
              repeat: Infinity,
              delay: Math.random() * 5,
            }}
          />
        ))}

        {/* Interactive light effect */}
        <motion.div 
          className="absolute inset-0 bg-gradient-to-br from-indigo-500/5 to-transparent"
          style={{
            x: mousePosition.x * 100,
            y: mousePosition.y * 100,
          }}
          transition={{ type: "spring", damping: 30, stiffness: 200 }}
        />
      </div>

      {/* Content */}
      <div className="relative min-h-screen flex items-center justify-center p-4">
        <div className="w-full max-w-6xl grid md:grid-cols-2 gap-12 items-center">
          {/* Left side - Branding */}
          <motion.div 
            initial={{ opacity: 0, x: -20 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.5 }}
            className="hidden md:flex flex-col items-start justify-center space-y-10 pr-8"
          >
            <motion.div 
              className="flex items-center space-x-4"
              whileHover={{ scale: 1.05 }}
              transition={{ type: "spring", stiffness: 400, damping: 10 }}
            >
              <motion.div
                animate={controls}
                className="relative"
              >
                <div className="absolute inset-0 bg-gradient-to-r from-indigo-500 to-purple-500 rounded-full blur-xl opacity-70"></div>
                <Logo className="w-16 h-16 text-white relative z-10" />
              </motion.div>
              <h1 className="text-4xl font-bold text-white tracking-tight bg-clip-text text-transparent bg-gradient-to-r from-white to-white/70">
                RoomieSync
              </h1>
            </motion.div>
            <div className="space-y-8">
              <motion.h2 
                className="text-3xl font-medium text-white/90 tracking-tight"
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ delay: 0.2 }}
              >
                The Future of Living Space Management
              </motion.h2>
              <motion.p 
                className="text-xl text-white/80 leading-relaxed"
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ delay: 0.3 }}
              >
                Experience the next generation of roommate coordination with our AI-powered platform. Trusted by thousands of professionals worldwide.
              </motion.p>
              <motion.div 
                className="flex flex-col space-y-4"
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ delay: 0.4 }}
              >
                {[
                  { icon: "âœ¨", text: "AI-powered chore management" },
                  { icon: "ðŸ•’", text: "Smart schedule optimization" },
                  { icon: "ðŸ‘¥", text: "Advanced guest management" },
                  { icon: "ðŸ’¬", text: "Secure real-time communication" },
                  { icon: "ðŸ“Š", text: "Predictive expense analytics" },
                  { icon: "ðŸ””", text: "Contextual notification system" }
                ].map((feature, index) => (
                  <motion.div 
                    key={index}
                    className="flex items-center space-x-3 bg-white/5 rounded-xl p-4 backdrop-blur-sm border border-white/10 hover:border-white/20 transition-all duration-300"
                    whileHover={{ 
                      scale: 1.02, 
                      backgroundColor: "rgba(255,255,255,0.1)",
                      boxShadow: "0 0 20px rgba(99, 102, 241, 0.3)"
                    }}
                    transition={{ type: "spring", stiffness: 400, damping: 10 }}
                  >
                    <span className="text-2xl">{feature.icon}</span>
                    <span className="text-white/90 font-medium">{feature.text}</span>
                  </motion.div>
                ))}
              </motion.div>

              {/* Trust indicators */}
              <motion.div
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                transition={{ delay: 0.6 }}
                className="pt-4"
              >
                <p className="text-white/60 text-sm mb-3">Trusted by professionals worldwide</p>
                <div className="flex space-x-6">
                  {["Forbes", "TechCrunch", "Wired", "Inc."].map((company, i) => (
                    <motion.div
                      key={i}
                      className="text-white/40 text-sm font-medium"
                      whileHover={{ opacity: 0.8 }}
                    >
                      {company}
                    </motion.div>
                  ))}
                </div>
              </motion.div>

              {/* Stats */}
              <motion.div
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                transition={{ delay: 0.7 }}
                className="grid grid-cols-3 gap-4 pt-4"
              >
                {[
                  { value: "10K+", label: "Active Users" },
                  { value: "98%", label: "Satisfaction" },
                  { value: "24/7", label: "Support" }
                ].map((stat, i) => (
                  <motion.div
                    key={i}
                    className="bg-white/5 rounded-lg p-3 text-center backdrop-blur-sm border border-white/10"
                    whileHover={{ 
                      backgroundColor: "rgba(255,255,255,0.1)",
                      boxShadow: "0 0 15px rgba(99, 102, 241, 0.2)"
                    }}
                  >
                    <div className="text-xl font-bold text-white">{stat.value}</div>
                    <div className="text-xs text-white/60">{stat.label}</div>
                  </motion.div>
                ))}
              </motion.div>
            </div>
          </motion.div>

          {/* Right side - iPhone Frame & Auth Form */}
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5, delay: 0.2 }}
            className="w-full max-w-[375px] mx-auto"
          >
            {/* iPhone Frame */}
            <div className="relative mx-auto border-[14px] border-black rounded-[2.5rem] h-[600px] max-w-[375px] shadow-2xl">
              {/* Notch */}
              <div className="absolute top-0 w-full h-6 bg-black">
                <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 w-20 h-4 bg-black rounded-full">
                  <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 w-12 h-1 bg-gray-800 rounded-full"></div>
                </div>
              </div>

              {/* Screen Content */}
              <div className="relative w-full h-full overflow-y-auto bg-gradient-to-br from-slate-900 to-slate-800 rounded-xl">
                <div className="p-6">
                  <div className="md:hidden flex items-center justify-center space-x-4 mb-8">
                    <Logo className="w-12 h-12 text-white" />
                    <h1 className="text-2xl font-bold text-white tracking-tight">RoomieSync</h1>
                  </div>
                  <div className="text-center mb-8">
                    <motion.h2 
                      className="text-2xl font-bold text-white tracking-tight"
                      initial={{ opacity: 0, y: -20 }}
                      animate={{ opacity: 1, y: 0 }}
                      transition={{ delay: 0.3 }}
                    >
                      {title}
                    </motion.h2>
                    {subtitle && (
                      <motion.p 
                        className="mt-3 text-sm text-white/70"
                        initial={{ opacity: 0, y: -20 }}
                        animate={{ opacity: 1, y: 0 }}
                        transition={{ delay: 0.4 }}
                      >
                        {subtitle}
                      </motion.p>
                    )}
                  </div>
                  <motion.div 
                    className="backdrop-blur-sm"
                    initial={{ opacity: 0, scale: 0.95 }}
                    animate={{ opacity: 1, scale: 1 }}
                    transition={{ delay: 0.5 }}
                  >
                    {children}
                  </motion.div>
                </div>
              </div>

              {/* Home Indicator */}
              <div className="absolute bottom-1 left-1/2 transform -translate-x-1/2 w-28 h-1 bg-gray-800 rounded-full"></div>
            </div>
          </motion.div>
        </div>
      </div>
    </div>
  );
};

export default AuthLayout;
