import React from 'react';
import Logo from '../common/Logo';
import { motion } from 'framer-motion';

interface AuthLayoutProps {
  children: React.ReactNode;
  title: string;
  subtitle?: string;
}

const AuthLayout: React.FC<AuthLayoutProps> = ({ children, title, subtitle }) => {
  return (
    <div className="min-h-screen relative overflow-hidden bg-gradient-to-br from-slate-900 via-indigo-900 to-slate-900">
      {/* Animated background elements */}
      <div className="absolute inset-0 overflow-hidden">
        {/* Subtle grid pattern */}
        <div className="absolute w-full h-full bg-[url('data:image/svg+xml;base64,')] opacity-20" />

        {/* Animated gradient orbs */}
        <motion.div
          className="absolute -top-1/2 -left-1/2 w-full h-full bg-gradient-to-br from-indigo-500/10 to-blue-500/10 rounded-full blur-3xl"
          animate={{
            scale: [1, 1.2, 1],
            rotate: [0, 90, 0],
          }}
          transition={{
            duration: 20,
            repeat: Infinity,
            ease: "linear"
          }}
        />
        <motion.div
          className="absolute -bottom-1/2 -right-1/2 w-full h-full bg-gradient-to-br from-purple-500/10 to-indigo-500/10 rounded-full blur-3xl"
          animate={{
            scale: [1.2, 1, 1.2],
            rotate: [90, 0, 90],
          }}
          transition={{
            duration: 20,
            repeat: Infinity,
            ease: "linear"
          }}
        />

        {/* Floating particles */}
        {[...Array(20)].map((_, i) => (
          <motion.div
            key={i}
            className="absolute w-1 h-1 bg-white/30 rounded-full"
            style={{
              left: `${Math.random() * 100}%`,
              top: `${Math.random() * 100}%`,
            }}
            animate={{
              y: [0, -20, 0],
              opacity: [0.3, 0.8, 0.3],
            }}
            transition={{
              duration: 3 + Math.random() * 5,
              repeat: Infinity,
              delay: Math.random() * 5,
            }}
          />
        ))}
      </div>

      {/* Content */}
      <div className="relative min-h-screen flex items-center justify-center p-4">
        <div className="w-full max-w-6xl grid md:grid-cols-2 gap-12 items-center">
          {/* Left side - Branding */}
          <motion.div 
            initial={{ opacity: 0, x: -20 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.5 }}
            className="hidden md:flex flex-col items-start justify-center space-y-10 pr-8"
          >
            <motion.div 
              className="flex items-center space-x-4"
              whileHover={{ scale: 1.05 }}
              transition={{ type: "spring", stiffness: 400, damping: 10 }}
            >
              <Logo className="w-16 h-16 text-white" />
              <h1 className="text-4xl font-bold text-white tracking-tight">RoomieSync</h1>
            </motion.div>
            <div className="space-y-8">
              <motion.h2 
                className="text-3xl font-medium text-white/90 tracking-tight"
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ delay: 0.2 }}
              >
                The Professional Living Space Management Platform
              </motion.h2>
              <motion.p 
                className="text-xl text-white/80 leading-relaxed"
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ delay: 0.3 }}
              >
                Streamline your shared living experience with our enterprise-grade platform. Trusted by thousands of professionals worldwide.
              </motion.p>
              <motion.div 
                className="flex flex-col space-y-4"
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ delay: 0.4 }}
              >
                {[
                  { icon: "âœ¨", text: "Enterprise-grade chore management" },
                  { icon: "ðŸ•’", text: "Advanced schedule optimization" },
                  { icon: "ðŸ‘¥", text: "Professional guest management" },
                  { icon: "ðŸ’¬", text: "Secure real-time communication" },
                  { icon: "ðŸ“Š", text: "Comprehensive expense analytics" },
                  { icon: "ðŸ””", text: "Intelligent notification system" }
                ].map((feature, index) => (
                  <motion.div 
                    key={index}
                    className="flex items-center space-x-3 bg-white/5 rounded-xl p-4 backdrop-blur-sm border border-white/10"
                    whileHover={{ 
                      scale: 1.02, 
                      backgroundColor: "rgba(255,255,255,0.1)",
                      borderColor: "rgba(255,255,255,0.2)"
                    }}
                    transition={{ type: "spring", stiffness: 400, damping: 10 }}
                  >
                    <span className="text-2xl">{feature.icon}</span>
                    <span className="text-white/90 font-medium">{feature.text}</span>
                  </motion.div>
                ))}
              </motion.div>

              {/* Trust indicators */}
              <motion.div
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                transition={{ delay: 0.6 }}
                className="pt-4"
              >
                <p className="text-white/60 text-sm mb-3">Trusted by professionals worldwide</p>
                <div className="flex space-x-6">
                  {["Forbes", "TechCrunch", "Wired", "Inc."].map((company, i) => (
                    <motion.div
                      key={i}
                      className="text-white/40 text-sm font-medium"
                      whileHover={{ opacity: 0.8 }}
                    >
                      {company}
                    </motion.div>
                  ))}
                </div>
              </motion.div>
            </div>
          </motion.div>

          {/* Right side - iPhone Frame & Auth Form */}
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5, delay: 0.2 }}
            className="w-full max-w-[375px] mx-auto"
          >
            {/* iPhone Frame */}
            <div className="relative mx-auto border-[14px] border-black rounded-[2.5rem] h-[600px] max-w-[375px] shadow-2xl">
              {/* Notch */}
              <div className="absolute top-0 w-full h-6 bg-black">
                <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 w-20 h-4 bg-black rounded-full">
                  <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 w-12 h-1 bg-gray-800 rounded-full"></div>
                </div>
              </div>

              {/* Screen Content */}
              <div className="relative w-full h-full overflow-y-auto bg-gradient-to-br from-slate-900 to-slate-800 rounded-xl">
                <div className="p-6">
                  <div className="md:hidden flex items-center justify-center space-x-4 mb-8">
                    <Logo className="w-12 h-12 text-white" />
                    <h1 className="text-2xl font-bold text-white tracking-tight">RoomieSync</h1>
                  </div>
                  <div className="text-center mb-8">
                    <motion.h2 
                      className="text-2xl font-bold text-white tracking-tight"
                      initial={{ opacity: 0, y: -20 }}
                      animate={{ opacity: 1, y: 0 }}
                      transition={{ delay: 0.3 }}
                    >
                      {title}
                    </motion.h2>
                    {subtitle && (
                      <motion.p 
                        className="mt-3 text-sm text-white/70"
                        initial={{ opacity: 0, y: -20 }}
                        animate={{ opacity: 1, y: 0 }}
                        transition={{ delay: 0.4 }}
                      >
                        {subtitle}
                      </motion.p>
                    )}
                  </div>
                  <motion.div 
                    className="backdrop-blur-sm"
                    initial={{ opacity: 0, scale: 0.95 }}
                    animate={{ opacity: 1, scale: 1 }}
                    transition={{ delay: 0.5 }}
                  >
                    {children}
                  </motion.div>
                </div>
              </div>

              {/* Home Indicator */}
              <div className="absolute bottom-1 left-1/2 transform -translate-x-1/2 w-28 h-1 bg-gray-800 rounded-full"></div>
            </div>
          </motion.div>
        </div>
      </div>
    </div>
  );
};

export default AuthLayout;
