import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import {
  CalendarIcon,
  PlusIcon,
  CheckCircleIcon,
  XCircleIcon,
  UserGroupIcon,
  ExclamationCircleIcon,
} from '@heroicons/react/24/outline';
import { tasks, household } from '../../api/client';

interface Task {
  id: string;
  title: string;
  description: string;
  dueDate: string;
  assignedToId?: string;
  status: 'PENDING' | 'COMPLETED' | 'OVERDUE';
  createdAt: string;
  updatedAt: string;
}

interface User {
  id: string;
  name: string;
}

interface Household {
  id: string;
  name: string;
  // Add other household properties as needed
}

interface ApiResponse<T> {
  data: T;
  status: number;
  message?: string;
}

const Chores: React.FC = () => {
  const [tasksList, setTasks] = useState<Task[]>([]);
  const [roommates, setRoommates] = useState<User[]>([]);
  const [selectedTask, setSelectedTask] = useState<string | null>(null);
  const [showAddModal, setShowAddModal] = useState(false);
  const [showAddAllModal, setShowAddAllModal] = useState(false);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [currentHouseholdId, setCurrentHouseholdId] = useState<string | null>(null);
  const [newTask, setNewTask] = useState({
    title: '',
    description: '',
    dueDate: '',
    assignedToId: '',
  });

  useEffect(() => {
    const fetchData = async () => {
      try {
        setIsLoading(true);
        setError(null);

        // Get current household
        const householdResponse = await household.getCurrent() as ApiResponse<Household>;
        if (householdResponse && householdResponse.data) {
          const householdId = householdResponse.data.id;
          setCurrentHouseholdId(householdId);

          // Fetch tasks for the household
          const tasksResponse = await tasks.getHouseholdTasks(householdId) as ApiResponse<Task[]>;
          if (tasksResponse && tasksResponse.data) {
            setTasks(tasksResponse.data);
          }

          // Fetch household members
          const membersResponse = await household.getMembers(householdId) as ApiResponse<User[]>;
          if (membersResponse && membersResponse.data) {
            setRoommates(membersResponse.data);
          }
        }
      } catch (err) {
        console.error('Error fetching data:', err);
        setError('Failed to load chores data. Please try again later.');
      } finally {
        setIsLoading(false);
      }
    };

    fetchData();
  }, []);

  const handleCreateTask = async () => {
    if (!currentHouseholdId) {
      setError('No household selected');
      return;
    }

    if (!newTask.title.trim()) {
      setError('Task title is required');
      return;
    }

    if (!newTask.dueDate) {
      setError('Due date is required');
      return;
    }

    try {
      setError(null);
      await tasks.create(currentHouseholdId, newTask);
      setShowAddModal(false);
      setNewTask({ title: '', description: '', dueDate: '', assignedToId: '' });

      // Refresh tasks list
      const response = await tasks.getHouseholdTasks(currentHouseholdId);
      if (response && response.data) {
        setTasks(response.data as Task[]);
      }
    } catch (err) {
      console.error('Error creating task:', err);
      setError('Failed to create task. Please try again.');
    }
  };

  const handleCreateTaskForAll = async () => {
    if (!currentHouseholdId) {
      setError('No household selected');
      return;
    }

    if (!newTask.title.trim()) {
      setError('Task title is required');
      return;
    }

    if (!newTask.dueDate) {
      setError('Due date is required');
      return;
    }

    try {
      setError(null);
      await tasks.createForAllMembers(currentHouseholdId, {
        title: newTask.title,
        description: newTask.description,
        dueDate: newTask.dueDate
      });
      setShowAddAllModal(false);
      setNewTask({ title: '', description: '', dueDate: '', assignedToId: '' });

      // Refresh tasks list
      const response = await tasks.getHouseholdTasks(currentHouseholdId);
      if (response && response.data) {
        setTasks(response.data as Task[]);
      }
    } catch (err) {
      console.error('Error creating tasks for all members:', err);
      setError('Failed to create tasks for all members. Please try again.');
    }
  };

  const handleUpdateTaskStatus = async (taskId: string, status: 'PENDING' | 'COMPLETED' | 'OVERDUE') => {
    try {
      setError(null);
      await tasks.updateStatus(taskId, status);

      // Refresh tasks list
      if (currentHouseholdId) {
        const response = await tasks.getHouseholdTasks(currentHouseholdId);
        if (response && response.data) {
          setTasks(response.data as Task[]);
        }
      }
    } catch (err) {
      console.error('Error updating task status:', err);
      setError('Failed to update task status. Please try again.');
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'COMPLETED':
        return 'bg-green-100 text-green-800';
      case 'OVERDUE':
        return 'bg-red-100 text-red-800';
      default:
        return 'bg-yellow-100 text-yellow-800';
    }
  };

  const formatDate = (dateString: string) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('en-US', { 
      weekday: 'short', 
      month: 'short', 
      day: 'numeric' 
    });
  };

  return (
    <div className="space-y-6">
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
        className="flex justify-between items-center"
      >
        <div>
          <h1 className="text-3xl font-bold text-neutral-900">Chores</h1>
          <p className="text-neutral-500 mt-2">Manage and track household chores</p>
        </div>
        <div className="flex space-x-4">
          <button
            onClick={() => setShowAddAllModal(true)}
            className="flex items-center px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700"
          >
            <UserGroupIcon className="w-5 h-5 mr-2" />
            Add for All
          </button>
          <button
            onClick={() => setShowAddModal(true)}
            className="flex items-center px-4 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700"
          >
            <PlusIcon className="w-5 h-5 mr-2" />
            Add Chore
          </button>
        </div>
      </motion.div>

      {error && (
        <div className="bg-red-50 border-l-4 border-red-500 p-4 mb-4">
          <div className="flex">
            <ExclamationCircleIcon className="h-5 w-5 text-red-500 mr-2" />
            <p className="text-red-700">{error}</p>
          </div>
        </div>
      )}

      {isLoading ? (
        <div className="text-center py-8">Loading chores...</div>
      ) : tasksList.length === 0 ? (
        <div className="text-center py-8 text-neutral-500">
          No chores added yet. Click the "Add Chore" button to get started.
        </div>
      ) : (
        <div className="grid gap-6">
          {tasksList.map((task) => (
            <motion.div
              key={task.id}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              className="bg-white rounded-lg shadow p-6"
            >
              <div className="flex items-center justify-between">
                <div className="flex-1">
                  <h3 className="text-lg font-medium text-neutral-900">{task.title}</h3>
                  {task.description && (
                    <p className="text-sm text-neutral-500 mt-1">{task.description}</p>
                  )}
                  <div className="flex items-center mt-2">
                    <CalendarIcon className="w-4 h-4 text-neutral-400 mr-1" />
                    <span className="text-sm text-neutral-500">
                      Due: {formatDate(task.dueDate)}
                    </span>
                  </div>
                  {task.assignedToId && (
                    <div className="mt-2">
                      <span className="text-sm text-neutral-500">
                        Assigned to: {roommates.find(r => r.id === task.assignedToId)?.name || 'Unknown'}
                      </span>
                    </div>
                  )}
                </div>
                <div className="flex flex-col items-end space-y-2">
                  <span className={`px-2 py-1 rounded-full text-xs font-medium ${getStatusColor(task.status)}`}>
                    {task.status}
                  </span>
                  <div className="flex space-x-2">
                    {task.status !== 'COMPLETED' && (
                      <button
                        onClick={() => handleUpdateTaskStatus(task.id, 'COMPLETED')}
                        className="p-1 text-green-600 hover:text-green-800"
                        title="Mark as completed"
                      >
                        <CheckCircleIcon className="w-5 h-5" />
                      </button>
                    )}
                    {task.status !== 'OVERDUE' && (
                      <button
                        onClick={() => handleUpdateTaskStatus(task.id, 'OVERDUE')}
                        className="p-1 text-red-600 hover:text-red-800"
                        title="Mark as overdue"
                      >
                        <XCircleIcon className="w-5 h-5" />
                      </button>
                    )}
                  </div>
                </div>
              </div>
            </motion.div>
          ))}
        </div>
      )}

      {/* Add Chore Modal */}
      {showAddModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white p-6 rounded-lg w-96">
            <h2 className="text-xl font-bold mb-4 text-neutral-900">Add New Chore</h2>
            <div className="space-y-4">
              <div>
                <label className="block text-sm font-medium text-neutral-700 mb-1">Title</label>
                <input
                  type="text"
                  placeholder="Chore Title"
                  className="w-full p-2 border rounded text-neutral-900"
                  value={newTask.title}
                  onChange={(e) => setNewTask({ ...newTask, title: e.target.value })}
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-neutral-700 mb-1">Description</label>
                <textarea
                  placeholder="Description"
                  className="w-full p-2 border rounded text-neutral-900"
                  value={newTask.description}
                  onChange={(e) => setNewTask({ ...newTask, description: e.target.value })}
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-neutral-700 mb-1">Due Date</label>
                <input
                  type="date"
                  className="w-full p-2 border rounded text-neutral-900"
                  value={newTask.dueDate}
                  onChange={(e) => setNewTask({ ...newTask, dueDate: e.target.value })}
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-neutral-700 mb-1">Assign To</label>
                <select
                  className="w-full p-2 border rounded text-neutral-900"
                  value={newTask.assignedToId}
                  onChange={(e) => setNewTask({ ...newTask, assignedToId: e.target.value })}
                >
                  <option value="">Select a roommate</option>
                  {roommates.map((roommate) => (
                    <option key={roommate.id} value={roommate.id}>
                      {roommate.name}
                    </option>
                  ))}
                </select>
              </div>
              <div className="flex justify-end space-x-2 mt-6">
                <button
                  onClick={() => setShowAddModal(false)}
                  className="px-4 py-2 bg-gray-200 text-gray-800 rounded hover:bg-gray-300"
                >
                  Cancel
                </button>
                <button
                  onClick={handleCreateTask}
                  className="px-4 py-2 bg-green-600 text-white rounded hover:bg-green-700"
                >
                  Create
                </button>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Add for All Modal */}
      {showAddAllModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white p-6 rounded-lg w-96">
            <h2 className="text-xl font-bold mb-4 text-neutral-900">Add Chore for All Members</h2>
            <div className="space-y-4">
              <div>
                <label className="block text-sm font-medium text-neutral-700 mb-1">Title</label>
                <input
                  type="text"
                  placeholder="Chore Title"
                  className="w-full p-2 border rounded text-neutral-900"
                  value={newTask.title}
                  onChange={(e) => setNewTask({ ...newTask, title: e.target.value })}
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-neutral-700 mb-1">Description</label>
                <textarea
                  placeholder="Description"
                  className="w-full p-2 border rounded text-neutral-900"
                  value={newTask.description}
                  onChange={(e) => setNewTask({ ...newTask, description: e.target.value })}
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-neutral-700 mb-1">Due Date</label>
                <input
                  type="date"
                  className="w-full p-2 border rounded text-neutral-900"
                  value={newTask.dueDate}
                  onChange={(e) => setNewTask({ ...newTask, dueDate: e.target.value })}
                />
              </div>
              <div className="flex justify-end space-x-2 mt-6">
                <button
                  onClick={() => setShowAddAllModal(false)}
                  className="px-4 py-2 bg-gray-200 text-gray-800 rounded hover:bg-gray-300"
                >
                  Cancel
                </button>
                <button
                  onClick={handleCreateTaskForAll}
                  className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
                >
                  Create
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Chores;
