import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { MagnifyingGlassIcon, UserPlusIcon, ExclamationCircleIcon } from '@heroicons/react/24/outline';
import { useAuth } from '../../context/AuthContext';
import { api } from '../../services/api';

interface User {
  id: string;
  name: string;
  email: string;
  bio?: string;
  profilePicture?: string;
  householdId?: string;
  householdName?: string;
}

const FindMembers: React.FC = () => {
  const { user } = useAuth();
  const [searchQuery, setSearchQuery] = useState('');
  const [users, setUsers] = useState<User[]>([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [successMessage, setSuccessMessage] = useState<string | null>(null);
  const [invitingUserId, setInvitingUserId] = useState<string | null>(null);

  useEffect(() => {
    const searchUsers = async () => {
      if (!searchQuery.trim()) {
        setUsers([]);
        return;
      }

      setLoading(true);
      setError(null);

      try {
        const response = await api.get<User[]>(`/users/search?q=${encodeURIComponent(searchQuery)}`);
        // Filter out the current user from results
        const filteredUsers = response.data.filter(u => u.id !== user?.id);
        setUsers(filteredUsers);
      } catch (err) {
        console.error('Error searching users:', err);
        setError('Failed to search users. Please try again later.');
      } finally {
        setLoading(false);
      }
    };

    const debounceTimer = setTimeout(searchUsers, 300);
    return () => clearTimeout(debounceTimer);
  }, [searchQuery, user?.id]);

  const handleInvite = async (userId: string) => {
    if (!user?.householdId) {
      setError('You must be part of a household to invite members.');
      return;
    }

    setInvitingUserId(userId);
    setError(null);
    setSuccessMessage(null);

    try {
      await api.post('/invitations', {
        householdId: user.householdId,
        userId: userId,
      });
      setSuccessMessage('Invitation sent successfully!');

      // Update the UI to show the user has been invited
      setUsers(prevUsers => 
        prevUsers.map(u => 
          u.id === userId ? { ...u, invited: true } : u
        )
      );
    } catch (err: any) {
      console.error('Error sending invitation:', err);
      if (err.response?.status === 409) {
        setError('This user has already been invited to your household.');
      } else if (err.response?.status === 403) {
        setError('You do not have permission to invite members to this household.');
      } else {
        setError('Failed to send invitation. Please try again later.');
      }
    } finally {
      setInvitingUserId(null);
    }
  };

  return (
    <div className="container mx-auto px-4 py-8">
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
      >
        <h1 className="text-3xl font-bold text-gray-900 mb-8">Find Members</h1>

        {/* Search Bar */}
        <div className="relative mb-8">
          <div className="relative">
            <input
              type="text"
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              placeholder="Search by name or email..."
              className="w-full px-4 py-3 pl-12 rounded-lg border border-gray-300 focus:ring-2 focus:ring-blue-500 focus:border-transparent"
            />
            <MagnifyingGlassIcon className="h-6 w-6 text-gray-400 absolute left-3 top-1/2 transform -translate-y-1/2" />
          </div>
        </div>

        {/* Error Message */}
        {error && (
          <div className="bg-red-50 border-l-4 border-red-400 p-4 mb-6 flex items-start">
            <ExclamationCircleIcon className="h-5 w-5 text-red-400 mr-2 mt-0.5" />
            <p className="text-red-700">{error}</p>
          </div>
        )}

        {/* Success Message */}
        {successMessage && (
          <div className="bg-green-50 border-l-4 border-green-400 p-4 mb-6">
            <p className="text-green-700">{successMessage}</p>
          </div>
        )}

        {/* Loading State */}
        {loading && (
          <div className="flex justify-center items-center py-8">
            <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-500"></div>
          </div>
        )}

        {/* Results */}
        {!loading && users.length > 0 && (
          <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-3">
            {users.map((user) => (
              <motion.div
                key={user.id}
                initial={{ opacity: 0, scale: 0.95 }}
                animate={{ opacity: 1, scale: 1 }}
                className="bg-white rounded-lg shadow-md p-6"
              >
                <div className="flex flex-col h-full">
                  <div className="flex items-center mb-4">
                    {user.profilePicture ? (
                      <img 
                        src={user.profilePicture} 
                        alt={user.name} 
                        className="h-12 w-12 rounded-full mr-3 object-cover"
                      />
                    ) : (
                      <div className="h-12 w-12 rounded-full bg-blue-100 flex items-center justify-center mr-3">
                        <span className="text-blue-500 font-medium text-lg">
                          {user.name.charAt(0).toUpperCase()}
                        </span>
                      </div>
                    )}
                    <div>
                      <h3 className="text-lg font-semibold text-gray-900">{user.name}</h3>
                      <p className="text-sm text-gray-500">{user.email}</p>
                    </div>
                  </div>
                  {user.bio && (
                    <p className="text-gray-600 mb-4 flex-grow">{user.bio}</p>
                  )}
                  <div className="flex items-center justify-between mt-4">
                    {user.householdName ? (
                      <span className="text-sm text-gray-500">
                        Member of: {user.householdName}
                      </span>
                    ) : (
                      <span className="text-sm text-gray-500">
                        Not in a household
                      </span>
                    )}
                    {!user.householdId && (
                      <button
                        onClick={() => handleInvite(user.id)}
                        disabled={invitingUserId === user.id}
                        className={`px-4 py-2 rounded-lg transition-colors ${
                          invitingUserId === user.id
                            ? 'bg-gray-300 cursor-not-allowed'
                            : 'bg-blue-500 hover:bg-blue-600 text-white'
                        }`}
                      >
                        {invitingUserId === user.id ? (
                          <span className="flex items-center">
                            <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                            Inviting...
                          </span>
                        ) : (
                          <span className="flex items-center">
                            <UserPlusIcon className="h-5 w-5 mr-1" />
                            Invite
                          </span>
                        )}
                      </button>
                    )}
                  </div>
                </div>
              </motion.div>
            ))}
          </div>
        )}

        {/* No Results */}
        {!loading && searchQuery && users.length === 0 && (
          <div className="text-center py-8">
            <p className="text-gray-500">No users found matching your search.</p>
          </div>
        )}

        {/* Empty State */}
        {!loading && !searchQuery && (
          <div className="text-center py-12 bg-gray-50 rounded-lg">
            <UserPlusIcon className="h-12 w-12 text-gray-400 mx-auto mb-4" />
            <h3 className="text-lg font-medium text-gray-900 mb-2">Find Roommates</h3>
            <p className="text-gray-500 max-w-md mx-auto">
              Search for users by name or email to invite them to your household.
            </p>
          </div>
        )}
      </motion.div>
    </div>
  );
};

export default FindMembers;
